#!/usr/bin/env bashhadoop jar \/home/instructor/hadoop-streaming/hadoop-streaming-2.7.3.jar \-D mapred.output.key.comparator.class=org.apache.hadoop.mapred.lib.KeyFieldBasedComparator \-D stream.map.output.field.separator=, \-D stream.num.map.output.key.fields=2 \-D map.output.key.field.separator=, \-D mapred.text.key.partitioner.options="-k1,2n" \-D mapred.text.key.comparator.options="-k1n -k2n" \-files mapper.py,reducer.py \-mapper mapper.py -reducer reducer.py \-input /user/dkrishna/retail/retail.dat \-output /user/jonathan.bichel/output